import com.disney.utils.Xls_Reader




def path = testRunner.testCase.testSuite.project.getPropertyValue("ExcelPath")
log.info "Excelsheet Path :"+path
Xls_Reader xls = new Xls_Reader(path)
// count rows
log.info "Total rows of data " + xls.getRowCount("TestResults")
// count cols
log.info "Total cols of data " + xls.getColumnCount("TestResults")
// read data
//log.info xls.getCellData("Result","TestID",1) // colName,rownum
//log.info xls.getCellData("Result",0,2) // colnum,rownum

// writing data - file should be closed
//xls.setCellData("LoginTest","Username",10,"Soapui")

def testId = null
def query = null
def result = null
def getTestInstanceTestStep = null
def testInstanceID = null
def updateResult = null
def testSetID = testRunner.testCase.testSuite.project.getPropertyValue("TestSetID")
def brand = testRunner.testCase.testSuite.project.getPropertyValue("Project")
def index = 0

log.info "Test set id - for  "+brand +" "+testSetID

for(int rNum=2;rNum<=xls.getRowCount("TestResults");rNum++){

		testId =  xls.getCellData("TestResults","TestID",rNum).trim()
		if(testId.contains(".")){
			index = testId.indexOf(".")
			testId = Integer.parseInt(testId.substring(0,index))
			}
          testId = testId.toInteger()
          if(testId>0){
		result =  xls.getCellData("TestResults","Result",rNum).trim()
		testRunner.testCase.testSuite.project.setPropertyValue("TestResult",result)
		query = "{cycle-id["+testSetID+"]; test-id["+testId+"]}";
		testRunner.testCase.testSuite.project.setPropertyValue("Query",query)
		getTestInstanceTestStep = testRunner.testCase.testSteps['GetTestInstance']
		getTestInstanceTestStep.run(testRunner,context)
		testInstanceID = testRunner.testCase.testSteps['TestInstanceID']
		testInstanceID.run(testRunner,context)
		updateResult = testRunner.testCase.testSteps['UpdateResult']
		updateResult.run(testRunner,context)
		log.info "Updated test result for test id "+testId+" as "+result
          }

}

import com.eviware.soapui.support.XmlHolder

def response = context.expand('${GetTestInstance#response}')

def testInstance = new XmlHolder(response)

def testInstanceId = testInstance.getNodeValue("//Field[@Name='id']/Value")

testRunner.testCase.testSuite.project.setPropertyValue("TestInstanceID",testInstanceId)

log.info "TestInstanceId : "+testInstanceId
